mapper.select:
select * from (
(select CONCAT(a.platform_name,'-',c.`name`) name from crowd_funding_company a LEFT JOIN company c on c.company_id=a.company_id where a.platform_name like CONCAT('%',#{keyword},'%') LIMIT 5) union (select CONCAT(a.platform_name,'-',c.`name`) name from crowd_funding_company a LEFT JOIN company c on c.company_id=a.company_id where c.`name` like CONCAT('%',#{keyword},'%') LIMIT 5)
) un limit 5
