mapper.select:
SELECT IFNULL(registered_capital_type,1) as registeredCapitalType, IFNULL(sum(registered_capital),0) as registeredCapital, COUNT(*) as companyNumber, YEAR (registered_date) AS year FROM company WHERE company_type = 13 AND registered_date IS NOT NULL
AND YEAR (registered_date) = #{year}
AND registered_capital_type = #{registeredCapitalType}
GROUP BY registered_capital_type, year ORDER BY year asc;

SELECT fle.free_trade_zone as freeTradeZone, COUNT(c.company_id) as companyNumber FROM company c LEFT JOIN finance_lease_extra fle ON c.company_id = fle.company_id WHERE c.company_type = 13
AND YEAR(registered_date) <= #{year}
GROUP BY fle.free_trade_zone;

SELECT company.company_id, a.risk_status AS riskStatusA, b.risk_status AS riskStatusB, c.risk_status AS riskStatusC, d.risk_status AS riskStatusD, company.`name` AS companyName, company.address AS address FROM company, area, finance_lease_risk a, finance_lease_risk b, finance_lease_risk c, finance_lease_risk d
company.area_id = area.area_id AND company.company_id = a.company_id AND company.company_id = b.company_id AND company.company_id = c.company_id AND company.company_id = d.company_id AND a.risk_type = 1 AND b.risk_type = 2 AND c.risk_type = 3 AND d.risk_type = 4 AND company.company_type = 13
AND area.name = #{areaName}
AND area.name = '黄浦区'
AND a.risk_status = #{riskA}
AND b.risk_status = #{riskB}
AND c.risk_status = #{riskC}
AND d.risk_status = #{riskD}
