mapper.insert:
INSERT INTO (
company_id, company_name, title, detail, create_by, update_by, update_date) VALUES (
#{companyId}, #{companyName}, #{title}, #{detail}, #{createBy}, #{updateBy}, #{updateDate} );

mapper.select:
SELECT
COUNT(*) AS recordTotal
pk AS pk, ifnull(company_id, '') AS companyId, ifnull(company_name, '') AS companyName, ifnull(title, '') AS title, ifnull(detail, '') AS detail, ifnull(create_by, '') AS createBy, ifnull(create_date, '') AS createDate, ifnull(update_by, '') AS updateBy, ifnull(update_date, '') AS updateDate
FROM
AND `company_id` = #{companyId}
AND `company_name` LIKE CONCAT('%',#{companyNameLike},'%')
AND `company_name` = #{companyName}
AND `update_by` = #{updateBy}
AND `update_date` >= #{beginTime}
AND `update_date` <= #{endTime}
ORDER BY update_date
DESC
LIMIT
#{pageNumber},
#{pageSize}
