mapper.select:
SELECT p.area_id AS areaId, (SELECT a.`name` FROM area a WHERE a.area_id=p.area_id LIMIT 1) AS areaName, p.`name` AS parkName, p.`park_id` AS parkId, b.`name` AS buildingName, b.`building_id` AS buildingId, DATE_FORMAT(p.create_date,'%Y.%c.%d') AS createDate FROM park p, building b, park_and_building_relation pabr
p.park_id=pabr.park_id and b.building_id=pabr.building_id
and p.park_id=#{parkId}
and b.`name`=#{buildingName}

SELECT b.building_id AS buildingId, b.`name` AS buildingName FROM building b, park_and_building_relation pabr WHERE b.building_id=pabr.building_id
AND pabr.park_id=#{parkId}
AND b.`name` LIKE CONCAT('%',#{buildingName},'%')
