mapper.select:
SELECT bb.* FROM ( SELECT aa.*, company_background.background FROM( SELECT base.* FROM ( SELECT company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, case company.risk_level when 1 then 5 when 2 then 2 when 3 then 3 when 4 then 4 else 9 end as riskLevel, -- company.is_new AS isNew, IF(TIMESTAMPDIFF(DAY,company.create_date,curdate()) <= #{newValue},1,0) AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, building.building_id AS buildingId, building.`name` AS buildingName FROM park,building,company_building,company,park_and_building_relation pabr WHERE park.park_id=#{parkId}
AND company.name LIKE CONCAT('%',#{companyName},'%')
AND park.park_id=pabr.park_id AND pabr.building_id=building.building_id AND building.building_id=company_building.building_id AND company_building.company_id=company.company_id
UNION ALL select company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, case company.risk_level when 1 then 5 when 2 then 2 when 3 then 3 when 4 then 4 else 9 end as riskLevel, -- company.is_new AS isNew, IF(TIMESTAMPDIFF(DAY,company.create_date,curdate()) <= #{newValue},1,0) AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, null AS buildingId, null AS buildingName from company where area_id=(select area_id from park where park_id=#{parkId})
AND company.name LIKE CONCAT('%',#{companyName},'%')
and company_id not in ( select company_id from company_building where company_building.building_id in (select building.building_id from building,park_and_building_relation pabr where building.building_id=pabr.building_id and pabr.park_id=#{parkId}))
) AS base WHERE 1=1
AND base.buildingName LIKE CONCAT('%',#{buildingName},'%')
AND base.isNew=#{isNew}
AND base.companyType=#{companyType}
AND base.riskLevel=#{riskLevel}
) AS aa LEFT JOIN ( SELECT company_id,GROUP_CONCAT(background)background FROM company_background GROUP BY company_id ) AS company_background ON aa.companyId=company_background.company_id)AS bb WHERE 1=1
AND bb.background LIKE CONCAT('%',#{background},'%')
ORDER BY bb.riskLevel ASC, bb.isNew DESC,bb.registeredCapital DESC
LIMIT #{beginNumber},#{pageSize}

SELECT count(1) AS counts FROM ( SELECT aa.*, company_background.background FROM( SELECT base.* FROM ( SELECT company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, case company.risk_level when 1 then 5 when 2 then 2 when 3 then 3 when 4 then 4 else 9 end as riskLevel, -- company.is_new AS isNew, IF(TIMESTAMPDIFF(DAY,company.create_date,curdate()) <= #{newValue},1,0) AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, building.building_id AS buildingId, building.`name` AS buildingName FROM park,building,company_building,company,park_and_building_relation pabr WHERE park.park_id=#{parkId}
AND company.name LIKE CONCAT('%',#{companyName},'%')
AND park.park_id=pabr.park_id AND pabr.building_id=building.building_id AND building.building_id=company_building.building_id AND company_building.company_id=company.company_id
UNION ALL select company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, case company.risk_level when 1 then 5 when 2 then 2 when 3 then 3 when 4 then 4 else 9 end as riskLevel, -- company.is_new AS isNew, IF(TIMESTAMPDIFF(DAY,company.create_date,curdate()) <= #{newValue},1,0) AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, null AS buildingId, null AS buildingName from company where area_id=(select area_id from park where park_id=#{parkId})
AND company.name LIKE CONCAT('%',#{companyName},'%')
and company_id not in ( select company_id from company_building where company_building.building_id in (select building.building_id from building,park_and_building_relation pabr where building.building_id=pabr.building_id and pabr.park_id=#{parkId}))
) AS base WHERE 1=1
AND base.buildingName LIKE CONCAT('%',#{buildingName},'%')
AND base.isNew=#{isNew}
AND base.companyType=#{companyType}
AND base.riskLevel=#{riskLevel}
) AS aa LEFT JOIN ( SELECT company_id,GROUP_CONCAT(background)background FROM company_background GROUP BY company_id ) AS company_background ON aa.companyId=company_background.company_id)AS bb WHERE 1=1
AND bb.background LIKE CONCAT('%',#{background},'%')

SELECT p.park_id AS parkId, p.`name` AS `name`, p.area_id AS areaId, p.img_url AS imgUrl, IF(IFNULL(tmp.park_id,0)=0,0,1) AS isSelected FROM park p LEFT JOIN (SELECT p1.park_id, p1.`name`, p1.area_id, p1.img_url FROM park p1
p1.area_id=#{areaId}
AND p1.park_id IN (SELECT pr.park_id FROM park_range pr WHERE pr.user_id=#{userId})
) tmp on p.park_id=tmp.park_id
