mapper.select:
SELECT bb.* FROM ( SELECT aa.*, company_background.background FROM( SELECT base.* FROM ( SELECT company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, if(isnull(company.risk_level)=1,9,company.risk_level) AS riskLevel, company.is_new AS isNew, company.registered_capital AS registeredCapital, building.building_id AS buildingId, building.`name` AS buildingName FROM park,building,company_building,company WHERE park.area_id=#{areaId} AND park.park_id=building.park_id AND building.building_id=company_building.building_id AND company_building.company_id=company.company_id UNION ALL select company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, if(isnull(company.risk_level)=1,9,company.risk_level) AS riskLevel, company.is_new AS isNew, company.registered_capital AS registeredCapital, null AS buildingId, null AS buildingName from company where area_id=#{areaId} and company_id not in (select company_id from company_building ) ) AS base WHERE 1=1
AND base.buildingName LIKE CONCAT('%',#{buildingName},'%')
AND base.isNew=#{isNew}
AND base.companyType=#{companyType}
AND base.riskLevel=#{riskLevel}
) AS aa LEFT JOIN
( SELECT company_id,GROUP_CONCAT(background)background FROM company_background GROUP BY company_id ) AS
company_background ON aa.companyId=company_background.company_id)AS bb WHERE 1=1
AND bb.background=#{background}
ORDER BY bb.isNew DESC,bb.riskLevel ASC,bb.registeredCapital DESC
