mapper.select:
SELECT bb.* FROM ( SELECT aa.*, company_background.background FROM( SELECT base.* FROM ( SELECT company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, if(isnull(company.risk_level)=1,9,company.risk_level) AS riskLevel, company.is_new AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, building.building_id AS buildingId, building.`name` AS buildingName FROM park,building,company_building,company WHERE park.area_id=#{areaId}
AND park.name=#{parkName}
AND company.name LIKE CONCAT('%',#{companyName},'%')
AND park.park_id=building.park_id AND building.building_id=company_building.building_id AND company_building.company_id=company.company_id UNION ALL select company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, if(isnull(company.risk_level)=1,9,company.risk_level) AS riskLevel, company.is_new AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, null AS buildingId, null AS buildingName from company where area_id=#{areaId}
AND company.name LIKE CONCAT('%',#{companyName},'%')
and company_id not in ( select company_id from company_building where building_id in (select building_id from building where park_id=(select park_id from park where area_id=#{areaId}
AND name=#{parkName} )) ) ) AS base WHERE 1=1
AND base.buildingName LIKE CONCAT('%',#{buildingName},'%')
AND base.isNew=#{isNew}
AND base.companyType=#{companyType}
AND base.riskLevel=#{riskLevel}
) AS aa LEFT JOIN ( SELECT company_id,GROUP_CONCAT(background)background FROM company_background GROUP BY company_id ) AS company_background ON aa.companyId=company_background.company_id)AS bb WHERE 1=1
AND bb.background LIKE CONCAT('%',#{background},'%')
ORDER BY bb.riskLevel ASC, bb.isNew DESC,bb.registeredCapital DESC
LIMIT #{beginNumber},#{pageSize}

SELECT count(1) AS counts FROM ( SELECT aa.*, company_background.background FROM( SELECT base.* FROM ( SELECT company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, if(isnull(company.risk_level)=1,9,company.risk_level) AS riskLevel, company.is_new AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, building.building_id AS buildingId, building.`name` AS buildingName FROM park,building,company_building,company WHERE park.area_id=#{areaId}
AND park.name=#{parkName}
AND company.name LIKE CONCAT('%',#{companyName},'%')
AND park.park_id=building.park_id AND building.building_id=company_building.building_id AND company_building.company_id=company.company_id UNION ALL select company.company_id AS companyId, company.`name` AS companyName, company.legal_person AS legalPerson, company.company_type AS companyType, if(isnull(company.risk_level)=1,9,company.risk_level) AS riskLevel, company.is_new AS isNew, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.registered_date AS registeredDate, company.address AS address, null AS buildingId, null AS buildingName from company where area_id=#{areaId}
AND company.name LIKE CONCAT('%',#{companyName},'%')
and company_id not in ( select company_id from company_building where building_id in (select building_id from building where park_id=(select park_id from park where area_id=#{areaId}
AND name=#{parkName}
)) ) ) AS base WHERE 1=1
AND base.buildingName LIKE CONCAT('%',#{buildingName},'%')
AND base.isNew=#{isNew}
AND base.companyType=#{companyType}
AND base.riskLevel=#{riskLevel}
) AS aa LEFT JOIN ( SELECT company_id,GROUP_CONCAT(background)background FROM company_background GROUP BY company_id ) AS company_background ON aa.companyId=company_background.company_id)AS bb WHERE 1=1
AND bb.background LIKE CONCAT('%',#{background},'%')
