mapper.select:
SELECT company.`name` AS `name`, company.company_type AS industry, company.legal_person AS legalPerson, company.registered_date AS registeredDate, company.registered_capital AS registeredCapital, company.registered_capital_type AS registeredCapitalType, company.address AS address FROM company, company_background WHERE company.company_id = company_background.company_id
and company.company_type = #{exportCondition.industry}
and company.risk_level = #{exportCondition.currentLevel}
and company.registered_capital >= #{exportCondition.registeredCapitalMin}
and #{exportCondition.registeredCapitalMax} >= company.registered_capital
and company.registered_date >= #{exportCondition.registeredDateMin}
and #{exportCondition.registeredDateMax} >= company.registered_date
and company.area_id = #{exportCondition.areaId}
LIMIT #{pageBean.offset}, #{pageBean.pageSize}

SELECT
  COUNT(1)
FROM
    company,
    company_background
WHERE
    company.company_id = company_background.company_id

        and company.company_type = #{exportCondition.industry}


        and company.risk_level = #{exportCondition.currentLevel}


        and company.registered_capital >= #{exportCondition.registeredCapitalMin}


        and #{exportCondition.registeredCapitalMax} >= company.registered_capital


        and company.registered_date >= #{exportCondition.registeredDateMin}


        and #{exportCondition.registeredDateMax} >= company.registered_date


        and company.area_id = #{exportCondition.areaId}
