mapper.select:
SELECT a.plat_name AS platName, a.company_name AS companyName, c.legal_person AS legalPerson, c.registered_date AS registeredDate, c.registered_capital AS registereCapital, c.registered_capital_type AS registereCapitalType, c.address AS address, a.create_date AS createDate FROM ( SELECT * FROM ( SELECT * FROM plat_list where 1 = 1
AND plat_name like #{platName}
AND company_name like #{companyName}
ORDER BY create_date DESC LIMIT 99999999999999 ) T GROUP BY T.plat_name ) a LEFT JOIN company c ON a.company_name = c.`name`
left join china_area ca on a.area_id = ca.area_code where ca.county = #{areaName}
ORDER BY
c.registered_date c.registered_capital a.create_date a.create_date DESC,a.plat_name ASC
desc
asc

select platName from ( SELECT
a.plat_name AS platName, a.company_name AS companyName, c.legal_person AS legalPerson, c.registered_date AS registeredDate, c.registered_capital AS registereCapital, c.registered_capital_type AS registereCapitalType, c.address AS address, a.create_date AS createDate FROM ( SELECT * FROM ( SELECT * FROM plat_list where 1 = 1
AND plat_name like #{platName}
AND company_name like #{companyName}
ORDER BY create_date DESC LIMIT 99999999999999 ) T GROUP BY T.plat_name ) a LEFT JOIN company c ON a.company_name = c.`name` ORDER BY a.create_date DESC, a.plat_name ASC )as bbb group by platName having count(1)>1
